
Vtiger_BaseValidator_Js("Vtiger_PhoneValidator_Js",{},{error:"",validate:function(){var c=this.fieldInfo;var b=c.val();var a=b.replace(/[\(\)\.\-\ ]/g,"");if(b==""){this.getEmptyPhoneNumberError()}else{if(isNaN(parseInt(a))){this.getPhoneNumberIllegalCharacterError()}else{if(!(a.length==10)){this.getPhoneNumberWrongLengthError()}}}},getEmptyPhoneNumberError:function(){this.error="You didn't enter a phone number.\n";return this.error},getPhoneNumberIllegalCharacterError:function(){this.error="The phone number contains illegal characters.\n";return this.error},getPhoneNumberWrongLengthError:function(){this.error="The phone number is the wrong length. Make sure you included an area code.\n";return this.error}});